mapper.select:
SELECT pw 
FROM member 
WHERE id = #{id} 
AND email = #{email}

mapper.update:
UPDATE member 
SET pw = #{pw} 
WHERE id = #{id}

mapper.select:
	SELECT * FROM member 
    WHERE pw = #{pw}
    AND id = #{id}

	SELECT admin FROM member 
    WHERE pw = #{pw}
    AND id = #{id}

SELECT count(*) 
FROM member 
WHERE id = #{id}
