mapper.select:
SELECT id
FROM member
WHERE name = #{name}
AND email = #{email}

SELECT pw
FROM member
WHERE id = #{id}
AND email = #{email}

mapper.update:
UPDATE member
SET pw = #{pw}
WHERE id = #{id}

mapper.select:
SELECT * FROM member
WHERE pw = #{pw}
AND id = #{id}
AND leave = 0

SELECT admin FROM member
WHERE pw = #{pw}
AND id = #{id}
